import {createSlice} from "@reduxjs/toolkit";


const initialState = {
    user: null,
    imie : '',
    nazwisko : '',
    isLogged : false
}

export const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {
        setUser: (state, action) => {
            state.user = action.payload;
            state.imie = action.payload.Imie;
            state.nazwisko = action.payload.Nazwisko;
            state.isLogged = true;
        },
        deSetUser : (state) => {
            state.user = null;
            state.imie = "";
            state.nazwisko = "";
            state.isLogged = false;
        },
        getUser: (state) => {
            return state.user
        }
    }
});
export const {setUser, deSetUser, getUser} = userSlice.actions;
export default userSlice.reducer;
