import {createSlice} from "@reduxjs/toolkit";
import user from "../../Model/User"

const initialState = {
    user: null,
    imie : '',
    nazwisko : '',
    isFoged : false
}

export const userSlice = {
    name: 'user',
    initialState,
    reducers: {
        setUser: (state, action) => {
            state.imie = action.payload.Imie;
            state.nazwisko = action.payload.Nazwisko;
        },
        deSetUser : (state) => {
            state.user = null;
        },
        getUser: (state) => {
            return state.user
        }
    }
}
export const {setUser, deSetUser, getUser} = userSlice.actions;
export default userSlice.reducer;
