import {setUser} from "../features/user/userSlice";
import {useState} from "react";
const userList = () =>{
    const  [users, setUsers] = useState()

    // const fetchUsers = async () => {
    //     try {
    //         const response = await fetch('http://localhost/users');
    //         const users = await response.json();
    //         return users;
    //     } catch (error) {
    //         console.error('Błąd pobierania danych użytkowników:', error);
    //         return [];
    //     }
    // };



    const generateUserListHTML = (users) => {
        return users.map((user) => {
            return `
      <div key="${user.id}">
        <p>ID: ${user.id}</p>
        <p>Imię: ${user.imie}</p>
        <p>Nazwisko: ${user.nazwisko}</p>
        <p>Rodzaj: ${user.rodzaj}</p>
        ${user.rodzaj === 'Klient' ? `<button onclick="deleteUser(${user.id})">Usuń</button>` : ''}
        <p>Adres: ${user.adres}</p>
      </div>
    `;
        }).join('');
    };

    return(
        //fetchUsers(),
        setUsers([
            {
                "id": 1,
                "imie": "John",
                "nazwisko": "Doe",
                "rodzaj": "Klient",
                "adres": "ul. Example 123"
            },
            {
                "id": 2,
                "imie": "Jane",
                "nazwisko": "Smith",
                "rodzaj": "Administrator",
                "adres": "ul. Sample 456"
            },
            {
                "id": 3,
                "imie": "Bob",
                "nazwisko": "Johnson",
                "rodzaj": "Klient",
                "adres": "ul. Test 789"
            }
        ]),
        generateUserListHTML()
    )
}
export default userList
